        if (buf.buf.len == 0) {
            return res;
        }
        var offset: usize = 0;
        while (buf.hasNext(offset, 0)) {
            const tag = try buf.readTagAt(offset);
            offset += tag.size;
            switch (tag.number) {
                NetworkPacketContextWire.IID_WIRE => {
                    const result = try buf.readUInt64(offset);
                    offset += result.size;
                    res._iid = result.value;
                },
                NetworkPacketContextWire.CTX_WIRE => {
                    const result = try buf.readBytes(offset);
                    offset += result.size;
                    res._ctx_buf = result.value;
                },
                else => {
                    offset = try buf.skipData(offset, tag.wire);
                },
            }
        }
        return res;
    }
    pub fn sourceBytes(self: *const @This()) []const u8 {
        return self.buf.buf;
    }
    pub inline fn getIid(self: *const NetworkPacketContextReader) u64 {
        return self._iid;
    }
    pub fn getCtx(self: *const NetworkPacketContextReader) gremlin.Error!NetworkPacketEventReader {
        if (self._ctx_buf) |buf| {
            return try NetworkPacketEventReader.init(buf);
        }
        return try NetworkPacketEventReader.init(&[_]u8{});
    }
};
